generated.css:
html {
  font-size: 10px;
  background: #fff;
  font-family: 'Lato', sans-serif;
}

body,html {
  margin: 0;
  padding: 0;

}

h1 {
  color: #000;
  margin: 40px;
  text-align: center;
  font-size: 60px;
}

.center {
  display: flex;
  justify-content: center;
  align-items: center;
}

a {
  margin: 10px;
  background-color: blue;
  cursor: pointer;
  padding: 20px 40px;
  border-radius: 5px;
  font-size: 20px;
}

a,
a:visited {
  text-decoration: none;
  color: #fff;
}
a:hover {
  background-color: darkBlue;

}

.keys {
  display: flex;
  flex: 1;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  margin-bottom: 30px;
}

.key {
  border: .4rem solid blue;
  border-radius: .5rem;
  margin: 1rem;
  font-size: 1.5rem;
  padding: 1rem .5rem;
  transition: all .07s ease;
  width: 10rem;
  text-align: center;
  color: white;
  background:darkblue;
  cursor: pointer;
  flex: 1;
  min-width: 50px;
  max-width: 80px;
  height: 150px;
  font-family: 'Lato', sans-serif;
}

.playing {
  transform: scale(1.1);
  border-color: lightblue;
  box-shadow: 0 0 1rem lightblue;
}

kbd {
  display: block;
  font-size: 6rem;
  font-family: 'Lato', sans-serif;
}
